sub get_text {
	my $vendor = shift;
	my $txt = $TEXT{$vendor} ? $TEXT{$vendor} : '';
	$txt =~ s/-!-/$separator/g;
	return $txt;
}

sub get_icon {
	my $vendor = shift;
	return exists $ICON{$vendor} ? $ICON{$vendor} : '';
}

